package Velocity;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String city="Kolhapur";
		
		switch(city)
		{
		case "Mumbai": System.out.println("Visit Gate way Of India");
		break;
		case "Kolhapur": System.out.println("Visit Mahalaxmi Temple");
		break;
		case "Pune": System.out.println("Visit ShaniwarWada");
		break;
		default: System.out.println("Not visited correct city");
		break;
					
		}

	}

}
